script:

var t = null;  
function start() {   
    if(t == null) {     
        t = setInterval(animate, 10);   
    } 
}  
function stop() {   
    if(t != null) {     
        clearInterval(t);     
        t = null;   
    } 
}  
function animate() {   
    var circle = document.getElementById("left");   
    var xforms = circle.getAttribute("transform");   
    var parts = xforms.replace(/[^0-9\s-.]/g, '').split(" ");   
    var firstX = parseFloat(parts[0]),       
        firstY = parseFloat(parts[1]);   
    var newCX = .1 + firstX;   
    if(newCX > 30) {     
        newCX = -30;   
    }   
    circle.setAttribute("transform", `translate(${newCX}, ${firstY})`);
}
